mapper.select:

select 

from ce_integrante
where c_cct = #{cCct,jdbcType=INTEGER}
  and c_sesion = #{cSesion,jdbcType=SMALLINT}
  and csc_integrante = #{cscIntegrante,jdbcType=SMALLINT}

mapper.delete:

delete from ce_integrante
where c_cct = #{cCct,jdbcType=INTEGER}
  and c_sesion = #{cSesion,jdbcType=SMALLINT}
  and csc_integrante = #{cscIntegrante,jdbcType=SMALLINT}
