mapper.select:

select 

from ce_programa
where c_cct = #{cCct,jdbcType=INTEGER}
  and c_sesion = #{cSesion,jdbcType=SMALLINT}
  and c_programa = #{cPrograma,jdbcType=SMALLINT}

mapper.delete:

delete from ce_programa
where c_cct = #{cCct,jdbcType=INTEGER}
  and c_sesion = #{cSesion,jdbcType=SMALLINT}
  and c_programa = #{cPrograma,jdbcType=SMALLINT}
