mapper.select:

select 

from ce_recursos_padres
where c_cct = #{cCct,jdbcType=INTEGER}
  and c_sesion = #{cSesion,jdbcType=SMALLINT}
  and id_consecutivor = #{idConsecutivor,jdbcType=SMALLINT}

mapper.delete:

delete from ce_recursos_padres
where c_cct = #{cCct,jdbcType=INTEGER}
  and c_sesion = #{cSesion,jdbcType=SMALLINT}
  and id_consecutivor = #{idConsecutivor,jdbcType=SMALLINT}
