mapper.select:

select 

from apec_necesidades_educativas
where c_apec = #{cApec,jdbcType=INTEGER}
  and c_reunion = #{cReunion,jdbcType=SMALLINT}
  and c_apoyo = #{cApoyo,jdbcType=INTEGER}

mapper.delete:

delete from apec_necesidades_educativas
where c_apec = #{cApec,jdbcType=INTEGER}
  and c_reunion = #{cReunion,jdbcType=SMALLINT}
  and c_apoyo = #{cApoyo,jdbcType=INTEGER}
