mapper.select:

select 

from c_comunidad_escolar
where cve_cct = #{cveCct,jdbcType=CHAR}
  and cve_turno = #{cveTurno,jdbcType=SMALLINT}
  and periodo = #{periodo,jdbcType=CHAR}

mapper.delete:

delete from c_comunidad_escolar
where cve_cct = #{cveCct,jdbcType=CHAR}
  and cve_turno = #{cveTurno,jdbcType=SMALLINT}
  and periodo = #{periodo,jdbcType=CHAR}
