mapper.select:
select b.c_sesion,a.c_normalidad, a.descrip_normalidad, b.opcion_nunca,b.opcion_casi_nunca,b.opcion_casi_siempre,b.opcion_siempre
  from c_normalidad a,
       outer ce_normalidad b
 where a.c_normalidad = b.c_normalidad
   and b.c_cct =  #{cCct,jdbcType=INTEGER}
   and b.c_sesion in (3,4)

	select *
   	  from ce_evaluaciones
	where c_cct =#{cCct,jdbcType=INTEGER}

	  select b.*, a.descrip_compromiso
	  from c_compromisos a,
	       ce_compromisos b
	 where b.id_compromiso = a.c_compromiso
	   and b.c_cct =  #{cCct,jdbcType=INTEGER}
       and b.c_sesion in ( 3,4)


	  select b.*, a.nom_evento
        from c_evento a,
             ce_eventos b
       where b.c_evento = a.c_evento
         and c_cct =  #{cCct,jdbcType=INTEGER}
	     and c_sesion in ( 3,4)


    	select *
			from ce_preguntas_estatal
		 where cct_id= #{cCct}


    	select *
			from ce_preguntas_municipal
		 where cct_id= #{cCct}


    	select b.*, a.descrip_candidato 
        from c_candidatos a,
             ce_estimulos_municipal b
       where b.candidato_municipal = a.c_candidato
         and c_cct =  #{cCct}
	         and c_sesion =4

select *
	from ce_recursos_padres
 where c_cct= #{cCct}


    	select *
			from ce_pregunta_recursos_padres
		 where cct_id= #{cCct}


select count(*) as numero
from ce_integrante
where c_cct=#{cCct}
